import { State } from "./state";
import { Patient } from "../types";

export type Action =
    {
      type: "UPDATE_PATIENT";
      payload: Patient;
    }
  | {
      type: "SET_PATIENT_LIST";
      payload: Patient[];
    }
  | {
      type: "ADD_PATIENT";
      payload: Patient;
    };

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "SET_PATIENT_LIST":
      return {
        ...state,
        patients: {
          ...action.payload.reduce(
            (memo, patient) => ({ ...memo, [patient.id]: patient }),
            {}
          ),
          ...state.patients
        }
      };
    case "ADD_PATIENT":
      return {
        ...state,
        patients: {
          ...state.patients,
          [action.payload.id]: action.payload
        }
      };
    case "UPDATE_PATIENT":
      return{
        ...state,
        patients:{
        ...state.patients,
        [action.payload.id]: action.payload
        }
      }
    default:
      return state;
  }
};

export const importAllPatients =(patientListFromApi: Patient[])=>{
  return{
    type: "SET_PATIENT_LIST",
    payload: patientListFromApi
  }

}
