import React from "react";
import {OccupationalHealthcareFormEntries} from "./index";
import {Field, Formik, Form} from "formik";
import {useStateValue} from "../state";
import {TextField,DiagnosisSelection } from "../AddPatientModal/FormField";

interface Props{
    onSubmit:(values:OccupationalHealthcareFormEntries )=>void;
    closeModal:()=>void
}

const AddOccupationalHealthcareForm: React.FC<Props>=({onSubmit, closeModal})=>{
    const [{diagnoses}]=useStateValue()
    return(
        <Formik initialValues={{
            description: "",
            date: "",
            specialist:"",
            diagnosisCodes: [],
            type: "OccupationalHealthcare",
            employerName: "",
            sickLeave:{
                startDate:"",
                endDate:""
            }
        }}
        onSubmit={onSubmit}
        validate={
            values=>{
                const requiredError="Field is required";
                const errors: {[field:string]:string}={}
                if(!values.description){
                    errors.name=requiredError
                }
                if(!values.date){
                    errors.date=requiredError
                }
                if(values.specialist){
                    errors.specialist=requiredError
                }
                if(!values.employerName){
                    errors.employerName =requiredError
                }
                return errors
            }}>
            {
                ({isValid, dirty})=>{
                    return (
                        <Form className="form ui">
                             <Field
                            label="specialist"
                            placeholder="SPECIALIST"
                            name="specialist"
                            component={TextField}
                            />
                        </Form>
                    )
                }
            }    

        </Formik>
    )
}

export default AddOccupationalHealthcareForm;
