import React from "react";
import {HealthCheckFormEntries} from "./index";
import {Field, Formik, Form} from "formik";
import {Grid, Button} from "semantic-ui-react";
import {TextField, NumberField} from "../AddPatientModal/FormField";

interface Props{
    onSubmit: (values:HealthCheckFormEntries)=>void,
    closeModal: ()=>void,
}

const AddHealthCheckForm:React.FC<Props>=({onSubmit, closeModal})=>{
    return(
        <Formik initialValues={{
            description: "",
            date: "",
            specialist: "",
            diagnosisCodes: [],
            type: "HealthCheck",
            healthCheckRating: 0,
        }} 
        onSubmit={onSubmit}
        validate={
            values=>{
                const requiredError= "Field is required";
                const errors: {[field: string]: string}={}
                if(!values.description){
                    errors.name=requiredError;
                }
                if(!values.date){
                    errors.description =requiredError;
                }
                if(!values.specialist){
                    errors.specialist =requiredError;
                }
                return errors;
            }}>
            {
                ({isValid, dirty})=>{
                    return(
                        <Form className="form ui">
                            <Field
                            label="description"
                            placeholder= "description"
                            name="description"
                            component={TextField}
                            />

                        </Form>
                    )
                }
            }
        </Formik>
    )
}
export default AddHealthCheckForm
