import React from "react";
import {HospitalEntry} from "../types";
import {useStateValue} from "../state/state";
import {Icon} from "semantic-ui-react";

const HospitalDisplay: React.FC<{entry:HospitalEntry, showIconType: any}>=({entry,showIconType})=>{
    const [{diagnoses}] =useStateValue()
    // console.log(entry)
    if(!diagnoses){
        return null
    }
    return(
        <section>
            <h3>{entry.date}, <Icon className={showIconType(entry.type)}/></h3>
            <p>{entry.description}</p>
            <h4>Diagnoses:</h4>
            <ul>
                {
                    entry.diagnosisCodes!== undefined? entry.diagnosisCodes.map(each=>
                        <li key={each}>{entry.diagnosisCodes}: {diagnoses[each].name} </li>
                        ):null
                }

            </ul>
            <h4>Discharged: {entry.discharge.date}</h4>
            <p>{entry.discharge.criteria}</p> 
        </section>
    )
}
export default HospitalDisplay;
